def transform_to_dataframe(event_log, parameters):
    data, feature_names = log_to_features.apply(event_log, variant=log_to_features.Variants.TRACE_BASED, parameters = parameters)
    #print(feature_names)

    pd_data = pd.DataFrame(data, columns=feature_names)
    return pd_data

def read_event_log(event_log_name = 'BPI17'):
    if event_log_name == 'BPI17':
        event_log, parameters = read_BPI17()
    elif event_log_name == 'BPI12':
        event_log, parameters = read_BPI12()
    elif event_log_name == 'ticketing':
        event_log, parameters = read_ticketing()
    elif event_log_name == 'RoadTraffic':
        event_log, parameters = read_road_traffic()
    elif event_log_name == 'Assignment2':
        event_log, parameters = read_BPI2()
    else:
        print('Undefined event log name')
        return None
    parameters["add_case_identifier_column"] = True
    parameters["enable_case_duration"] = True
    parameters["enable_succ_def_representation"] = False

    pd_data = transform_to_dataframe(event_log=event_log, parameters=parameters)

    return pd_data